import { Component } from '@angular/core';
import { MyserviceService } from '../myservice.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  firstForm: FormGroup;
  secondForm: FormGroup;
  constructor(private service: MyserviceService) {
    this.firstForm = new FormGroup({
      address: new FormControl('', Validators.required),
      address2: new FormControl('', Validators.required),
      city: new FormControl('', Validators.required),
      state: new FormControl('', Validators.required),
      postalCode: new FormControl('', Validators.required),
    });
    this.secondForm = new FormGroup({
      address: new FormControl('', Validators.required),
    });
  }
  dishes: any[] = [];
  dis_len: any;
  showbtn: boolean = false;
  resturant: any;
  price: any;
  ngOnInit() {
    const dishesData = localStorage.getItem('Dishes');
    if (dishesData !== null) {
      this.dishes = JSON.parse(dishesData);
      this.dis_len = this.dishes.length;
      console.log(this.dishes);
    }
    if (this.dis_len > 0) {
      this.showbtn = true;
    }

    this.resturant = localStorage.getItem('resturant');
  }

  onSubmit() {
    console.log(this.firstForm.value);
  }
  des(item: any) {
    item.qty -= 1;
    localStorage.setItem('Dishes', JSON.stringify(item));
    if (item.qty == 0) {
      localStorage.removeItem('Dishes');
    }
  }
  incr(item: any) {
    item.qty += 1;
    localStorage.setItem('Dishes', JSON.stringify(item));
  }
}
