import { Component } from '@angular/core';
import { MyserviceService } from '../myservice.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  firstForm: FormGroup;
  secondForm: FormGroup;
  constructor(private service: MyserviceService) {
    this.firstForm = new FormGroup({
      address: new FormControl('', Validators.required),
      address2: new FormControl('', Validators.required),
      city: new FormControl('', Validators.required),
      state: new FormControl('', Validators.required),
      postal code: new FormControl('', Validators.required),
    });
    this.secondForm = new FormGroup({
      address: new FormControl('', Validators.required),
    });
  }
  dishes: any[] = [];
  ngOnInit() {
    const dishesData = localStorage.getItem('Dishes');
    if (dishesData !== null) {
      this.dishes = JSON.parse(dishesData);
      console.log(this.dishes);
    }
  }
  onSubmit() {}
}
<!-- <p>cart works!</p>
 -->
<body>
  <p>
    <mat-toolbar>
      <span>Secure Checkout</span>
    </mat-toolbar>
  </p>
  <!-- <div *ngFor="let item of dishes">
    <mat-card>{{ item.dish }}</mat-card>
    <p>quantity</p>
  </div> -->

  <mat-stepper orientation="vertical" [linear]="true" #stepper>
    <mat-step [stepControl]="firstForm"
      ><form [formGroup]="firstForm" (ngSubmit)="onSubmit()">
        <mat-form-field class="example-full-width">
          <mat-label>Address</mat-label>
          <input type="text" formControlName="address" matInput />
        </mat-form-field>

        <mat-form-field class="example-full-width">
          <mat-label>Address2</mat-label>
          <input type="text" formControlName="address2" matInput />
        </mat-form-field>
        <mat-form-field class="example-full-width">
          <mat-label>City</mat-label>
          <input type="text" formControlName="city" matInput />
        </mat-form-field>

        <mat-form-field class="example-full-width">
          <mat-label>State</mat-label>
          <input type="text" formControlName="state" matInput />
        </mat-form-field>

        <mat-form-field class="example-full-width">
          <mat-label>Postal code</mat-label>
          <input type="text" formControlName="postal code" matInput />
        </mat-form-field>
      </form>
    </mat-step>
    <mat-step>
      <form [formGroup]="secondForm" (ngSubmit)="onSubmit()">
        <button mat-button matStepperNext type="submit">Submit</button>
      </form></mat-step
    >
  </mat-stepper>
</body>
