import { SocialAuthService } from '@abacritt/angularx-social-login';
import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { DashboardComponent } from '../dashboard/dashboard.component';

@Component({
  selector: 'app-dailog-login',
  templateUrl: './dailog-login.component.html',
  styleUrls: ['./dailog-login.component.css']
})
export class DailogLoginComponent {

  password: any;
  email: any;
  loginstatus: any;
  login: boolean = false;
  user: any;
  loggedIn!: boolean;



  constructor(private authService: SocialAuthService,private dash:DashboardComponent) { }

  ngOnInit() {
    this.authService.authState.subscribe((user) => {
      this.user = user;
      this.loggedIn = (user != null);
      this.loginstatus = this.loggedIn
      console.log(this.user)
      console.log(this.loggedIn)

    });


  }


  onSubmit(details: NgForm) {
    console.log(details.value)
  }
}
