import { Component } from '@angular/core';
import { MyserviceService } from '../myservice.service';
import { FormControl, FormGroup, Validator } from '@angular/forms';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  firstForm: FormGroup;
  secondForm: FormGroup;
  constructor(private service: MyserviceService) {
    this.firstForm = new FormGroup({
      name: new FormControl('', Validators.required),
      email: new FormControl('', Validators.required),
    });
    this.secondForm = new FormGroup({
      name: new FormControl('', Validators.required),
      email: new FormControl('', Validators.required),
    });
  }
  dishes: any[] = [];
  ngOnInit() {
    const dishesData = localStorage.getItem('Dishes');
    if (dishesData !== null) {
      this.dishes = JSON.parse(dishesData);
      console.log(this.dishes);
    }
  }
  onSubmit() {}
}
<!-- <p>cart works!</p>
 -->
<body>
  <p>
    <mat-toolbar>
      <span>Secure Checkout</span>
    </mat-toolbar>
  </p>
  <!-- <div *ngFor="let item of dishes">
    <mat-card>{{ item.dish }}</mat-card>
    <p>quantity</p>
  </div> -->

  <mat-stepper orientation="vertical" [linear]="true" #stepper>
    <mat-step [stepControl]="firstForm"
      ><form [formGroup]="firstForm" (ngSubmit)="onSubmit()">
        <label>Name:</label>
        <input type="text" formControlName="name" />

        <label>Email:</label>
        <input type="email" formControlName="email" />

        <button type="submit">Submit</button>
      </form>
    </mat-step>
    <mat-step [stepControl]="secondForm">
      <form [formGroup]="secondForm" (ngSubmit)="onSubmit()">
        <label>Name:</label>
        <input type="text" formControlName="name" />

        <label>Email:</label>
        <input type="email" formControlName="email" />

        <button type="submit">Submit</button>
      </form></mat-step
    >
    <mat-step>
      <ng-template matStepLabel>Done</ng-template>
      <p>You are now done.</p>
    </mat-step>
  </mat-stepper>
</body>
