import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { DailogLoginComponent } from '../dailog-login/dailog-login.component';
import { DailogSignupComponent } from '../dailog-signup/dailog-signup.component';
import { MyserviceService } from '../myservice.service';

@Component({
  selector: 'app-biryani-bay',
  templateUrl: './biryani-bay.component.html',
  styleUrls: ['./biryani-bay.component.css'],
})
export class BiryaniBayComponent {
  login!: boolean;
  user: any;
  userget: any;
  clicked: boolean = false;
  dishes: any[] = [];
  constructor(public dialog: MatDialog, private service: MyserviceService,private route:) {}
  ngOnInit(): void {
    this.service.check();
    this.service.datachangeobservable.subscribe((d) => (this.login = d));

    this.user = localStorage.getItem('Name');
    const user1 = this.user.replaceAll('"', ' ');
    this.userget = user1;
  }
  openDialog() {
    this.dialog.open(DailogLoginComponent);
  }
  opensignupDialog() {
    this.dialog.open(DailogSignupComponent);
  }

  Recommended: any = [
    { id: 1, dish: 'Chicken Cheese Grilled Sandwich', price: 130 },
    { id: 2, dish: 'Lassi Sweet', price: 130 },
    { id: 3, dish: 'Fruit Salad with Ice Cream', price: 130 },
    { id: 4, dish: 'Caramel Custard', price: 130 },
    { id: 5, dish: 'Fresh Lime Soda', price: 130 },
    { id: 6, dish: 'Lassi Salty', price: 130 },
    { id: 7, dish: 'Lassi Sweet', price: 130 },
    { id: 8, dish: 'Mountain Dew', price: 130 },
    { id: 9, dish: 'Pepsi', price: 130 },
  ];

  add_to_cart(item: any) {
    this.clicked = true;
    setTimeout(() => {
      this.clicked = true;
    }, 1000);
    this.dishes.push(item);
    localStorage.setItem('Dishes', JSON.stringify(this.dishes));
    console.log(localStorage.getItem('Dishes'));
  }
  logout() {
    localStorage.removeItem('pass');
    localStorage.removeItem('loginId');
  }
  cart() {

  }
}
