import { Component } from '@angular/core';
import { MyserviceService } from '../myservice.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  firstForm: FormGroup;
  secondForm: FormGroup;
  constructor(private service: MyserviceService) {
    this.firstForm = new FormGroup({
      address: new FormControl('', Validators.required),
      address2: new FormControl('', Validators.required),
      city: new FormControl('', Validators.required),
      state: new FormControl('', Validators.required),
      postalCode: new FormControl('', Validators.required),
    });
    this.secondForm = new FormGroup({
      address: new FormControl('', Validators.required),
    });
  }
  totalPrice: any[] = [];
  dishes: any;
  dis_len: any;
  showbtn: boolean = false;
  resturant: any;
  price: any;
  checklist: any[] = [];
  total = 0;
  ngOnInit() {
    if (this.dis_len > 0) {
      this.showbtn = true;
    }

    this.loaddata();
    this.dishes.forEach((obj: any) => {
      this.totalPrice.push(obj.price);
      console.log(obj.price);
    });
    console.log(this.totalPrice);
    this.resturant = localStorage.getItem('resturant');
  }
  loaddata() {
    const dishesData: any = localStorage.getItem('Dishes');

    if (dishesData) {
      try {
        const parsedData = JSON.parse(dishesData);
        if (typeof parsedData === 'object' && parsedData != null) {
          this.dishes = Object.values(parsedData);
          console.log('dishes', this.dishes);
        } else {
          console.log('error parsing not an array');
        }
      } catch (err) {
        console.log('error parshing stored data', err);
      }
      this.dis_len = this.dishes.length;
    }
  }

  onSubmit() {
    console.log(this.firstForm.value);
  }
  des(item: any) {
    item.qty -= 1;
    console.log('item', item.price);

    this.dishes.forEach((obj: any) => {
      if (item.id == obj.id) {
        obj.qty = item.qty;
        // obj.price = obj.price - lastprice;
      }
    });
    console.log(this.dishes);

    // this.totalPrice = item.price;
    localStorage.setItem('Dishes', JSON.stringify(item));
    if (item.qty == 0) {
      localStorage.removeItem('Dishes');
    }
  }
  incr(item: any) {
    item.qty += 1;
    this.dishes.forEach((obj: any) => {
      if (item.id == obj.id) {
        obj.qty = item.qty;
        obj.price = obj.price * item.qty;
      }
    });
    console.log(this.dishes);
    // item.price = item.price * item.qty;
    // console.log('orig price', item.price);
    // this.dishes.forEach((obj: any) => {
    //   if (obj.price == item.price) {
    //     this.totalPrice.forEach((item: any, index = 0) => {
    //       if (item[index] == item.price) {
    //         this.totalPrice.push(obj.price * item.qty);
    //         console.log('arr data', item);
    //         console.log('success');
    //       }
    //     });
    //   }
    // });
    console.log('updated price');

    // this.totalPrice = item.price;
    localStorage.setItem('Dishes', JSON.stringify(item));
  }
}
