import { Component } from '@angular/core';
import { OwlOptions } from 'ngx-owl-carousel-o';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  picture: any = [
    {id:1 ,imgUrl:"../../assets/images/biryani.png"},
    {id:2 ,imgUrl:"../../assets/images/Burgers.png"},
    {id:3 ,imgUrl:"../../assets/images/cakes.png"},
    {id:4 ,imgUrl:"../../assets/images/chicken.png"},
    {id:5 ,imgUrl:"../../assets/images/Dosa.png"},
    {id:6 ,imgUrl:"../../assets/images/fried rice.png"},
    {id:7 ,imgUrl:"../../assets/images/noodles.png"},
    {id:8 ,imgUrl:"../../assets/images/Paneer.png"},
    {id:9 ,imgUrl:"../../assets/images/Pizza.png"},
    {id:10 ,imgUrl:"../../assets/images/rolls.png"},
    {id:11 ,imgUrl:"../../assets/images/Sandwich.png"},
    {id:12 ,imgUrl:"../../assets/images/thali.png"},
  ]


  customOptions: OwlOptions = {
    loop: true,
    mouseDrag: false,
    touchDrag: false,
    pullDrag: false,
    dots: false,
    navSpeed: 700,
    navText: ['', ''],
    responsive: {
      0: {
        items: 1
      },
      400: {
        items: 2
      },
      740: {
        items: 3
      },
      940: {
        items: 4
      }
    },
    nav: true
  }
}
