<body>
  <p>
    <mat-toolbar>
      <span>Secure Checkout</span>
    </mat-toolbar>
  </p>
  <!-- <div *ngFor="let item of dishes">
    <mat-card>{{ item.dish }}</mat-card>
    <p>quantity</p>
  </div> -->

  <mat-stepper orientation="vertical" [linear]="isLinear" #stepper>
    <mat-step>
      <ng-template matStepLabel>Add Address</ng-template>

      <mat-label>Flat No.</mat-label>
      <div>
        <input
          matInput
          placeholder="Flat no."
          formControlName="firstCtrl"
          required
        />
        <button mat-button matStepperNext>Next</button>
      </div>
    </mat-step>
    <mat-step>
      <ng-template matStepLabel>select payment</ng-template>

      <div>
        <button mat-button matStepperNext>Next</button>
      </div>
    </mat-step>
  </mat-stepper>
</body>
