import { Component } from '@angular/core';
import { } from'@
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

 mobileNumber: string;
  otp: string;
  otpSent: boolean = false;

  constructor(private http: HttpClient) { }

  sendOTP() {
    this.http.post('http://api.example.com/send-otp', { mobile: this.mobileNumber })
      .subscribe((response: any) => {
        console.log(response);
        this.otpSent = true;
      });
  }

  verifyOTP() {
    this.http.post('http://api.example.com/verify-otp', { mobile: this.mobileNumber, otp: this.otp })
      .subscribe((response: any) => {
        console.log(response);
      });
  }

}
